from math import exp, abs
def bisection(f, a, b, tol):
    while abs(b - a) > tol:
        c = 0.5 * (a + b)
        if f(c) * f(a) < 0:
            b = c
        else:
            a = c
    return a

def roots(f, tol=1e-6):
    a = -2
    b = 2
    return bisection(f, a, b, tol)

f = lambda x: x**3 - x - 4
